mapper.select:
  	SELECT *
	FROM
	    (SELECT ROWNUM rn, a.*
	     FROM
	         (SELECT *
	          FROM users
	          ORDER BY userid
	          ) a
	     WHERE ROWNUM <= #{page} * #{pageSize}
	     )
	WHERE rn BETWEEN (#{page}-1) * #{pageSize} +1 AND #{page} * #{pageSize}

mapper.update:
  	UPDATE users 
  	SET usernm = #{usernm}, pass = #{pass}, reg_dt = #{reg_dt}, alias = #{alias}, addr1=#{addr1},
                 addr2 = #{addr2}, zipcode = #{zipcode}
	WHERE userid = #{userid}
